mapper.select:
select rm_id, jn_id, emp_no, emp_id, rm_name
	from (select * from chatroomjoin left join chatroom using(rm_id) where rm_id =#{rm_id})
	left join employee using(emp_no)

 select count(*)
	from (select * from chatroomjoin left join chatroom using(rm_id) where rm_id = #{rm_id})
	left join employee using(emp_no);
